insert into Prodotto(NomeProdotto,DescrizioneProdotto,Prezzo,Disponibile,Sconto,LinkImmagine,LinkImmagineCarosello,LinkImmagineCarrello) values ('Dragon Quest','Quando un’onda oscura si abbatte sulla città di Arba, i mostri che prima convivevano pacificamente con gli abitanti vengono travolti da una furia incontrollabile. Scegli tra il protagonista Lucyus o la protagonista Aurora e combatti al fianco di alcuni dei personaggi più amati della serie, come Alena, Bianca e Yangus, per calmare i mostri e riportare la pace nel regno.',26.12,1,15,'/img/home_dragon_quest.jpg','/img/carosello_dragon_quest.jpg','/img/carrello_dragon_quest.jpg')
insert into Prodotto(NomeProdotto,DescrizioneProdotto,Prezzo,Disponibile,LinkImmagine,LinkImmagineCarosello,LinkImmagineCarrello) values ('World of Final Fantasy','Con una grafica adorabile che conquisterà grandi e piccini, il gioco ti permette di raccogliere, allevare e far combattere mostri leggendari incolonnandoli l’uno sull’altro in vere e proprie torri di mostri. Ritrova i mostri più memorabili di FINAL FANTASY in questo mondo fantastico e colorato e vivi una storia epica insieme ai tuoi piccoli amici.',30.35,1,'/img/home_final_fantasy.jpg','/img/carosello_final_fantasy.jpg','/img/carrello_final_fantasy.jpg')
insert into Prodotto(NomeProdotto,DescrizioneProdotto,Prezzo,Disponibile,Sconto,LinkImmagine,LinkImmagineCarosello,LinkImmagineCarrello) values ('Odin Sphere','E'' un RPG d''azione in 2D a scorrimento orizzontale, ricco di mitologia nordica e l’atmosfera di un’opera wagneriana. La nuova versione è un remake completo in HD di quella classica originale per PS2 Odin Sphere di Vanillaware. Il regno di Valentine era il paese più potente del continente di Erion. La sua gente prosperava con il magico potere della Crystallization Cauldron, ma è stato distrutto in una notte fatale.',30.00,1,25,'/img/home_odin_sphere.jpg','/img/carosello_odin_sphere.jpg','/img/carrello_odin_sphere.jpg')
